INSERT INTO `content` (`id`, `provider_id`, `category_id`, `title`, `locale`, `text`, `publish_date`) VALUES (5503, 8, 3, 'Algeria Fights Back to Beat South Africa, 3-1', 'en', 'South Africa missed a penalty and conceded an own goal as top-ranked Algeria came from behind to win 3-1 in their African Nations Cup Group C game on Monday.<br />', '2015-01-19 22:53:03');
